local bundle = [[### assets/player.png ###
89504e470d0a1a0a0000000d4948445200000008000000080806000000c40fbe8b000000017352474200aece1ce9000000414944415428536d8e510a00300842f5fe8776386864ab9f48ed1501409248b2bab5aa27da0c83a4e7089852a1a25da1a33bc90b11d8ce7cc8498cc0fae47c6a120e2618400994574baa0000000049454e44ae426082
### run.lua ###


]]
for path, s in bundle:gmatch("### ([^ ]*) ###\n([^\n]*)\n") do
	local bytes = s:gsub("%x%x", function(digits) return string.char(tonumber(digits, 16)) end)
	local dir = path:match('.+/')
    if dir then love.filesystem.createDirectory(dir) end
	love.filesystem.write(path, bytes)
end

love.filesystem.load("run.lua")()
